import { Component } from 'react';

import logo from './logo.svg';
import './App.css';

class App extends Component {

  constructor(){
    super();

    this.state = {
      monsters: [ 
                  {
                    name:'Linda',
                    id:'123783'
                  },
                  {
                    name:'Frank',
                    id:'12375849'
                  },
                  {
                    name:'Jacky',
                    id:'123790'
                  },
                  {
                    name:'Phani',
                    id:'1237125'
                  },
                ]
    }

    };

  render(){
    return (
      <div className="App">
        {
          this.state.monsters.map((monster)=>{
            return <h1>{monster.name}</h1>;
          })
        }
      </div>
    );
  } 
}

export default App;
