import { Component } from 'react';

import logo from './logo.svg';
import './App.css';

class App extends Component {

  constructor(){
    super();
    console.log('constructor-in');

    this.state = {
      monsters: []
    }

    console.log('constructor-out');
  };

  componentDidMount() {
    console.log('componentDidMount-in');

    fetch('https://jsonplaceholder.typicode.com/users')
      .then(response => response.json())
      .then(users => {
        this.setState(()=>{
          return {monsters: users};
        },
        ()=>{
          console.log(this.state);
        })
      });

      console.log('componentDidMount-out');
  }

  render(){
    console.log("render-in");
    return (
      <div className="App">
        <input className='search-box' type='search' placeholder='Search Monsters'
          onChange={(event)=>{

            const filteredMonsters = this.state.monsters.filter((monster) => {
              return (monster.name.toLocaleLowerCase().includes(event.target.value.toLocaleLowerCase()))
            });

            this.setState(()=>{
              return {monsters: filteredMonsters}
            })

          }}
        />
        {
          this.state.monsters.map((monster)=>{
            return <div key={monster.id}> <h1>{monster.name}</h1> </div>;
          })
        }
      </div>
    );

  } 
}

export default App;
