import { Component } from 'react';

// import logo from './logo.svg';
import './App.css';
import CardList from './components/card-list/card-list.component';
import SearchBox from './components/search-box/search-box.component';
class App extends Component {

  constructor(){
    super();
    // console.log('constructor');

    this.state = {
      monsters: [],
      searchInput: ''
    }
  };

  componentDidMount() {
    // console.log('componentDidMount');

    fetch('https://jsonplaceholder.typicode.com/users')
      .then(response => response.json())
      .then(users => {
        this.setState(()=>{
          return {monsters: users};
        },
        // ()=>{
        //   console.log(this.state);
        // }
        )
      });
  }

  onSearchChange = (event)=>{

    const searchInput = event.target.value.toLocaleLowerCase();
    this.setState(()=>{
      return {searchInput};
    });

  };

  render(){
    // console.log("render");

    const{monsters,searchInput} = this.state;
    const{onSearchChange} = this;

    const filteredMonsters = monsters.filter((monster) => {
      return (monster.name.toLocaleLowerCase().includes(searchInput));
    });
    // console.log("Filtered:",filteredMonsters);
    return (
      <div className="App">
        <SearchBox onChangeHandler={onSearchChange} placeholder='search monsters' />
        {/* {
          filteredMonsters.map((monster)=>{
            return <div key={monster.id}> <h1>{monster.name}</h1> </div>;
          })
        } */}

        <CardList monsters={filteredMonsters} />
      </div>
    );
  }
}

export default App;
