import {useState, useEffect, Fragment} from 'react'
import Index from './components/index/index.component'
// import Item from './components/create/create.component'

const App = () => {

  const [name, setName] = useState('');
  const [desc, setDesc] = useState('');


  const todoItems = [
    {
      name: "First",
      description: "My First todo records"
    },
    {
      name: "Second",
      description: "My second todo records"
    },
    {
      name: "Third",
      description: "My third todo records"
    },
    {
      name: "Fourth",
      description: "My fourth todo records"
    },
  ]

  useEffect(() => {

  }, [])

  // const addItems = () => {
  //   todoItems.push({name: {name}, description:{desc}});
  // }

  localStorage.setItem('ToDo-List', JSON.stringify(todoItems));

  return (
    <div>

      <div className='form' key='tt'>
        <div key='kk'>
          <label>Name :</label>
          <input
            className='name-input'
            value={name}
            onChange={(event) => setName(event.target.value)}
          />
        </div>
        <div key='ll'>
          <label>Description :</label>
          <input
            className='desc-input'
            value={desc}
            onChange={(event) => setDesc(event.target.value)}
          />
        </div>
        {/* <button onClick={addItems()} type="button">Save</button> */}
      </div>

      <Index todoItems={todoItems} />

    </div>
  );

}

export default App;
