// import React from 'react'
import './App.css'
import {useState, useEffect, Fragment} from 'react'
import Index from './components/index/index.component'
// import Item from './components/create/create.component'

const App = () => {

  const [inpName, setInpName] = useState('');
  const [inpDesc, setInpDesc] = useState('');
  const [todoList, setTodoList] = useState([
    {
      id: 1,
      name: "First",
      description: "My First todo records"
    }
  ]);

  // const todoItems = [
  //   {
  //     name: "First",
  //     description: "My First todo records"
  //   },
  //   {
  //     name: "Second",
  //     description: "My second todo records"
  //   },
  //   {
  //     name: "Third",
  //     description: "My third todo records"
  //   },
  //   {
  //     name: "Fourth",
  //     description: "My fourth todo records"
  //   },
  // ]
  var temp = 2;

  const addItems = (inpName, inpDesc) => {
    // if(inpName === '' || inpDesc === '')
    //   return;
    setTodoList([...todoList, {id: 1, name: inpName, description: inpDesc}]);
    localStorage.setItem('ToDo-List', JSON.stringify(todoList));
  }

  localStorage.setItem('ToDo-List', JSON.stringify(todoList));

  return (
    <div>

      <div className='form'>
        <div>
          <label>Name :</label>
          <input
            className='name-input'
            value={inpName}
            onChange={(event) => setInpName(event.target.value)}
            required
          />
        </div>
        <div>
          <label>Description :</label>
          <input
            className='desc-input'
            value={inpDesc}
            onChange={(event) => setInpDesc(event.target.value)}
          />
        </div>
        <button onClick={() => addItems(inpName, inpDesc)} type="button">Save</button>
      </div>

      <Index todoItems={todoList} />

    </div>
  );

}

export default App;
