const Index = () => {

  const todoItem = JSON.parse(localStorage.getItem('ToDo-List'));

  return todoItem.map((item) => {
    var { id, name, description } = item;
    id = id+1;
    return (
      <table className='list-table'>
        <tr>
          <td>{id}</td>  
          <td>{name}</td>
          <td>{description}</td>
          <td>
            <button>Update</button>
          </td>
        </tr>
      </table>
    );

  });
};

export default Index;
