import './App.css'
import {useState, useEffect, Fragment} from 'react'
import Index from './components/index/index.component'
// import Item from './components/create/create.component'

const App = () => {

  const [name, setName] = useState('');
  const [desc, setDesc] = useState('');

  const addItems = () => {
    localStorage.setItem('Name', name);
    localStorage.setItem('Description', desc);
 };

  const todoItems = [
    {
      id: 1,
      name: "First",
      description: "My First todo records"
    },
    {
      id: 2,
      name: "Second",
      description: "My second todo records"
    },
    {
      id: 3,
      name: "Third",
      description: "My third todo records"
    },
    {
      id: 4,
      name: "Fourth",
      description: "My fourth todo records"
    },
  ]

  useEffect(() => {

  }, [])

  return (
    <div>
          <div className='form'>
            <div>
                <label>Name :</label>
                <input id="name" className='name-input' />
            </div>
            <div>
                <label>Description :</label>
                <input className='desc-input' />
            </div>
            <button onClick={addItems()} type="submit">Save</button>
          </div>

          <Index todoItems={todoItems} />
    </div>
  );
}

export default App;
