const Item = ({name,desc}) => {
    const addItems = () => {
        localStorage.setItem('Name', name);
        localStorage.setItem('Description', desc);
    }
    return(
        <div className='form'>
            <div>
                <label>Name :</label>
                <input className='name-input' value={name} />
            </div>
            <div>
                <label>Description :</label>
                <input className='desc-input' value={desc} />
                {/* <p>{name}+{desc}</p> */}
            </div>
            <button onClick={addItems()} type="submit">Save</button>
        </div>
    )
}

