  ]

  return (
    <div className='form'>
      <div >
        <label>Name :</label>
        <input className='name-input' />
      </div>
      <div >
        <label>Description :</label>
        <input className='desc-input' />
      </div>
      <button type="submit" onClick={updateList()} >Save</button>
    </div>
  );
}

export default App;
